__NUXT_JSONP__("/%E3%80%90%E3%82%BB%E3%82%AD%E3%83%A5%E3%83%AA%E3%83%86%E3%82%A3%E3%80%91ddos%E6%94%BB%E6%92%83-dos%E6%94%BB%E6%92%83", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A){return {data:[{post:{slug:"【セキュリティ】ddos攻撃-dos攻撃",description:"ふんわり理解していたDDoS攻撃 (とDoS攻撃) を調べる。DDoS と DoS の違い、歴史、背景など",title:"【セキュリティ】DDoS攻撃 (DoS攻撃)",date:"2021-12-17T00:01:23.340Z",tag:"セキュリティ",toc:[{id:q,depth:h,text:r},{id:s,depth:h,text:t},{id:u,depth:h,text:v},{id:i,depth:h,text:i}],body:{type:"root",children:[{type:b,tag:j,props:{id:q},children:[{type:b,tag:g,props:{href:"#ddos%E6%94%BB%E6%92%83%E3%81%A8%E3%81%AF",ariaHidden:k,tabIndex:l},children:[{type:b,tag:m,props:{className:[n,o]},children:[]}]},{type:a,value:r}]},{type:a,value:c},{type:b,tag:p,props:{},children:[{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:"サイバー攻撃の種類の一つ"}]},{type:a,value:c}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:b,tag:f,props:{},children:[{type:a,value:"DDoS = Distributed Denial of Service attack"}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"攻撃者は複数の機器(PC等)を踏み台にして特定の機器に一斉攻撃を仕掛けることができる。踏み台として利用される機器に関連性がないことから犯人が特定しづらいのが特徴。"}]},{type:a,value:c},{type:b,tag:"blockquote",props:{},children:[{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"DDoS攻撃は、IPAが公開している「情報セキュリティ10大脅威 2020」の10位にランクインしている「サービス妨害攻撃によるサービス停止」に関わるサイバー攻撃です。（参考：IPA 「"},{type:b,tag:g,props:{href:"https:\u002F\u002Fwww.ipa.go.jp\u002Fsecurity\u002Fvuln\u002F10threats2020.html",rel:[w,x,y],target:z},children:[{type:a,value:"情報セキュリティ10大脅威 2020"}]},{type:a,value:"」）"}]},{type:a,value:c}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"💡 似た名前の攻撃に「DoS攻撃」というものがある。これは一斉攻撃ではなく一台の危機から行う攻撃。(DoS = Denial of Services)"}]},{type:a,value:c},{type:b,tag:j,props:{id:s},children:[{type:b,tag:g,props:{href:"#ddos%E6%94%BB%E6%92%83%E3%81%AE%E7%A8%AE%E9%A1%9E",ariaHidden:k,tabIndex:l},children:[{type:b,tag:m,props:{className:[n,o]},children:[]}]},{type:b,tag:f,props:{},children:[{type:a,value:t}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"DDoS攻撃にはいくつか種類がある。命名に関しては「コンピュータがデータ送信するときのどの仕組みの部分」を攻撃したかにより決まる。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"(フラッド = Flood : 洪水)"}]},{type:a,value:c},{type:b,tag:p,props:{},children:[{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:c},{type:b,tag:d,props:{},children:[{type:b,tag:f,props:{},children:[{type:a,value:"SYNフラッド攻撃／FINフラッド攻撃"}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"コンピューターが通信を確立する際の手順である「接続要求（SYN）」や「切断要求（FIN）」に負荷をかける攻撃"}]},{type:a,value:c}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:c},{type:b,tag:d,props:{},children:[{type:b,tag:f,props:{},children:[{type:a,value:"ACKフラッド攻撃"}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"「応答（ACK）」を大量に送信して通信負荷をかける攻撃です。"}]},{type:a,value:c}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:c},{type:b,tag:d,props:{},children:[{type:b,tag:f,props:{},children:[{type:a,value:"UDPフラッド攻撃"}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"攻撃元のIPを偽装して大量のパケットを送ることで、回線の帯域幅をパンクさせるタイプの攻撃です。"}]},{type:a,value:c}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:c},{type:b,tag:d,props:{},children:[{type:b,tag:f,props:{},children:[{type:a,value:"DNSフラッド攻撃"}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"DNSサーバーに大量のリクエストを送ることで負荷をかけ、DNSの機能を著しく低下させる攻撃。"}]},{type:a,value:c}]},{type:a,value:c}]},{type:a,value:c},{type:b,tag:j,props:{id:u},children:[{type:b,tag:g,props:{href:"#ddos%E6%94%BB%E6%92%83%E3%81%AE%E8%A2%AB%E5%AE%B3%E4%BA%8B%E4%BE%8B",ariaHidden:k,tabIndex:l},children:[{type:b,tag:m,props:{className:[n,o]},children:[]}]},{type:a,value:v}]},{type:a,value:c},{type:b,tag:p,props:{},children:[{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:"2015年11月：東京五輪組織委員会のホームページがサイバー攻撃を受け、約12時間閲覧不能になる"}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:"2015年12月：一部の電気通信事業者で数時間に渡りDNSサーバーへの接続障害"}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:"2016年9月：サイバーセキュリティ専門ジャーナリストのブログに対して、マルウェア「Mirai」に感染したIoT機器約18万台からの大規模攻撃"}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:"2016年10月：米国Dyn社のDNSフラッドサーバーが大規模なDDoS攻撃を受け、DNSを利用している企業のサービスに障害が発生"}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:"2020年3月：HHS（米保健福祉省）のシステムに過剰な負荷をかけるDDoS攻撃（実害なし）"}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:b,tag:f,props:{},children:[{type:a,value:"2020年6月：米国のセキュリティサービス事業者Cloudflare（クラウドフレア）：最大毎秒7億5400万パケットに達する大規模DDoS攻撃（自動対応により被害なし）🎉🎉🎉🎉"}]}]},{type:a,value:c},{type:b,tag:e,props:{},children:[{type:a,value:"2020年6月：ロシアの中央選挙管理委員会：オンライン投票サービスを狙ったDDoS攻撃"}]},{type:a,value:c}]},{type:a,value:c},{type:b,tag:j,props:{id:i},children:[{type:b,tag:g,props:{href:"#%E3%81%9D%E3%81%AE%E4%BB%96",ariaHidden:k,tabIndex:l},children:[{type:b,tag:m,props:{className:[n,o]},children:[]}]},{type:a,value:i}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:b,tag:g,props:{href:"https:\u002F\u002Fbusiness.ntt-east.co.jp\u002Fcontent\u002Fcloudsolution\u002Fcolumn-185.html#:~:text=DDoS%E6%94%BB%E6%92%83%E3%81%A8%E3%81%AF%E3%80%81%E3%82%B5%E3%82%A4%E3%83%90%E3%83%BC,%E7%89%B9%E5%BE%B4%E3%81%AE%E4%B8%80%E3%81%A4%E3%81%A7%E3%81%99%E3%80%82",rel:[w,x,y],target:z},children:[{type:a,value:"DDoS攻撃とは？攻撃の目的や種類から実例と対策までを解説｜コラム｜クラウドソリューション｜サービス｜法人のお客さま｜NTT東日本"}]}]}]},dir:"\u002Fblog",path:"\u002Fblog\u002F【セキュリティ】ddos攻撃-dos攻撃",extension:".md",createdAt:A,updatedAt:A},_img:{}}],fetch:{},mutations:void 0}}("text","element","\n","p","li","strong","a",3,"その他","h3","true",-1,"span","icon","icon-link","ul","ddos攻撃とは","DDoS攻撃とは","ddos攻撃の種類","DDoS攻撃の種類","ddos攻撃の被害事例","DDoS攻撃の被害事例","nofollow","noopener","noreferrer","_blank","2022-02-12T09:31:26.438Z")));